import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';


const HooksApi=(props)=>
{
    //State management
    const [users,setUsers]=useState([]);


//RESTAPI_CALLS
useEffect(()=>{
          axios
            .get('http://localhost:3003/react173')
            .then( (apiresult)=> setUsers(apiresult.data) )
},[])

        return (<>

                <div className="container py-5">
                   

                   <table className="table table-dark mx-auto my-2">
                       <thead className="thead-light">
                           <tr>
                               <th>SNO</th>
                               <th>NAME</th>
                               <th>EMAIL</th>
                               <th>LOCATION</th>
                               <th>PHONE</th>
                               <th colSpan="3">ACTION</th>
                           </tr>
                       </thead>

                      <tbody>   {/*  step-4 */}
                            {
                                users.map( (res,index)=>{
                                    return(<tr key={index}>
                                                    <td>{index+1}</td>
                                                    <td>{res.name}</td>
                                                    <td>{res.email}</td>
                                                    <td>{res.location}</td>
                                                    <td>{res.phone}</td>   
                                                    <td> <Link to={`${props.match.path}/view/${res.id}`} className="btn btn-primary text-white">View</Link></td>
                                            </tr>)
                                })
                            }
                    </tbody>
                   </table>
                    
                
                </div>

        </>)
    }


export default HooksApi
