import React, { Component } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';

class Userprofile extends Component {

    constructor(props)
    {
        super();
        this.state={ 
                    users:{}//empty object for single object data...
                    }

        console.log(props);
    }



   
    
