              details+=<li>{ address[i] } </li>
            }
            return(details)
        }


        return (<>

            

                <div className="container py-5">
                    <h3 className="py-3 text-center"> 
                    USER PROFILE DETAILS: 

                     </h3>

                   <table className="table table-dark w-50 mx-auto my-2">
                       <tr>
                           <th>ID</th>
                           <td>{id}</td>
                       </tr>
                       
                       <tr>
                           <th>NAME</th>
                           <td>{name}</td>
                       </tr>
                       
                       <tr>
                           <th>USERNAME</th>
                           <td>{username}</td>
                       </tr>
                       
                       <tr>
                           <th>EMAIL</th>
                           <td>{email}</td>
                       </tr>

                       <tr>
                           <th>ADDRESS</th>
                           <td>
                               { 
                                    result()
                               }
                           </td>
                       </tr>


                       <tr>
                           <th>
                                <Link className="btn btn-primary" to="/restapi">GOTO BACK</Link>
                           </th>
                           <td></td>
                       </tr>
                       
                       
                   </table>
                    
                
                </div>

        </>)
    }


    //step-3
    componentDidMount()
    {
        axios.get(`https://jsonplaceholder.typicode.com/users/${this.props.match.params.userid}`).then( (apiresult)=> this.setState({  users: apiresult.data }) )
       
    }



}

export default Userprofile
