import React, { Component } from 'react'


const userdata=[
                {id:1, name:'kumar1', email:'kumar1@gmail.com'},
                {id:2, name:'kumar2', email:'kumar2@gmail.com'}
               ]


class AxoisHttpCalls extends Component {

    constructor()
    {
        super();
        this.state={ 
                    users:[]
         }
    }


    handleApiData=()=>
    {
        this.setState({  users: userdata })
    }
    
    render() {
        return (<>

                <div className="container py-5">
                    <h3 className="py-3 text-center"> 
                    AXIOS RESTAPI CALLS.....  
                    <button onClick={this.handleApiData}>Click</button> </h3>

                   <table className="table table-dark w-75 mx-auto my-2">
                       <thead className="table table-light">
                           <tr>
                               <th>ID</th>
                               <th>NAME</th>
                               <th>EMAIL</th>
                           </tr>
                       </thead>

                      <tbody>
                            {
                                this.state.users.map( (res,index)=>{
                                    return(<tr>
                                                    <td>{res.id}</td>
                                                    <td>{res.name}</td>
                                                    <td>{res.email}</td>
                                            </tr>)
                                })
                            }
                    </tbody>




                   </table>
                    
                        <br /><br /><br />
                       


                </div>

        </>)
    }

}

export default AxoisHttpCalls



