import React from 'react'

const Conditionalrenderings=()=> {

    //JS_DATE_OBJECT
const today=new Date().getDay();

switch(today)
{
    case 0: return(<h1>Today is Monday</h1>); break;
    case 1: return(<h1>Today is Monday</h1>); break;
    case 2: return(<h1>Today is Monday</h1>); break;
    case 3: return(<h1>Today is Wednesdau</h1>); break;
    case 4: return(<h1>Today is Thursday</h1>); break;
    case 5: return(<h1>Today is Friday</h1>); break;
    case 6: return(<h1>Today is Saturday</h1>); break;
    
}
   

    return(<>

        <div className="container p-5">
            <h3> Switch Conditional Rendering.... </h3> <br /><br />


            { today }
           

        </div>

        </>)

}

export default Conditionalrenderings
