import React, { useState,useEffect } from 'react';
import axios from 'axios';


const url="https://jsonplaceholder.typicode.com/photos";

const PaginationTask=(props)=> {

//State management
    const [Posts,setPosts]=useState([]);

//RESTAPI_CALLS
useEffect(async ()=>{
    await axios.get(url).then( (apiresult)=> setPosts(apiresult.data) )
},[])

    return(<>
        <div className="container py-5">

                <h2 className="text-center py-3">Pagination Task</h2>

            <div className="row">

                {
                     Posts.map( (res,index)=>{
                        return(
                        <div className="col-lg-4" key={index}>

                                <div className="card">
                                   
                                    <img src={res.thumbnailUrl} className="card-img-top" />
                                   
                                    <div className="card-body">
                                        <p className="card-text">
                                                {res.id}.${res.title}
                                        </p>
                                
                                    </div>
                                </div>
                        </div>
                        )
                    })
                }

            </div>


            
        </div>
    </>)
}

export default PaginationTask
