import React, { Component } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';

class AxoisHttpCalls extends Component {

    constructor(props)
    {
        super();
        this.state={ 
                    users:[] //empty array-for -multiple details
                    }

        console.log(props);
    }

    
    render() {
        
        return (<>

                <div className="container py-5">
                    <h3 className="py-3 text-center"> 
                    AXIOS RESTAPI CALLS.....  

                     </h3>

                   <table className="table table-dark mx-auto my-2">
                       <thead className="thead-light">
                           <tr>
                               <th>ID</th>
                               <th>NAME</th>
                               <th>USERNAME</th>
                               <th>EMAIL</th>
                               <th>PHONE</th>
                               <th colSpan="3">ACTION</th>
                           </tr>
                       </thead>

                      <tbody>   {/*  step-4 */}
                            {
                                this.state.users.map( (res,index)=>{
                                    return(<tr key={index}>
                                                    <td>{res.id}</td>
                                                    <td>{res.name}</td>
                                                    <td>{res.username}</td>
                                                    <td>{res.email}</td>
                                                    <td>{res.phone}</td>
                                                    <td> <Link to={`${this.props.match.path}/${res.id}`} className="btn btn-primary text-white">View</Link></td>
                                                    <td> <button className="btn btn-warning text-white">Edit</button></td>
                                                    <td> <button className="btn btn-danger text-white">Delete</button></td>
                                                
                                            </tr>)
                                })
                            }
                    </tbody>
                   </table>
                    
                
                </div>

        </>)
    }


    //step-3
    componentDidMount()
    {
        console.log("***componentDidMount*****");
        axios.get('http://localhost:3001/react173').then( (apiresult)=> this.setState({  users: apiresult.data }) )
       
    }



}

export default AxoisHttpCalls
