import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HooksApiView=(props)=>
{
    //State management
    const [users,setUsers]=useState({ 
        id:'',
        name:'',
        email:'',
        
    });


//RESTAPI_CALLS
useEffect(async ()=>{
          await  axios
            .get('http://localhost:3001/react173')
            .then( (apiresult)=> setUsers(apiresult.data) )
},[])

        return (<>

                <div className="container py-5">
                   

                   <table className="table table-dark mx-auto my-2">
                       <thead className="thead-light">
                           <tr>
                               <th>SNO</th>
                               <th>NAME</th>
                               <th>EMAIL</th>
                               <th>CITY</th>
                               <th>PHONE</th>
                               <th colSpan="3">ACTION</th>
                           </tr>
                       </thead>

                      <tbody>   {/*  step-4 */}
                            {
                                users.map( (res,index)=>{
                                    return(<tr key={index}>
                                                    <td>{index+1}</td>
                                                    <td>{res.name}</td>
                                                    <td>{res.email}</td>
                                                    <td>{res.city}</td>
                                                    <td>{res.phone}</td>   
                                                    <td> <Link to={`${props.match.path}/view/${res.id}`} className="btn btn-primary text-white">View</Link></td>
                                            </tr>)
                                })
                            }
                    </tbody>
                   </table>
                    
                
                </div>

        </>)
    }


export default HooksApiView
