import Axios from 'axios';
import React, { Component , setState } from 'react'

class Signup extends Component {

    constructor(props) {
        super(props);

        this.state=
        {
            name:'',
            email:'',
            city:'',
            course:'',
            phone:''
        }
    }


    //name_input
    HandleNameInput=(e)=>
    {
        this.setState({ name:e.target.value  })        
    }

    

    //email_input
    HandleEmailInput=(e)=>
    {
        this.setState({ email: e.target.value })
    }

    //city_input
    HandleCityInput=(e)=>
    {
        this.setState({ city: e.target.value })
    }

    //course_input
    HandleCourseInput=(e)=>
    {
           this.setState({ course: e.target.value })
    }


    //phone_input
    HandlePhoneInput=(e)=>
    {
        this.setState({ phone: e.target.value })
    }



//submit_button
HandleSubmitForm=()=>
{

    const formsdata={
        'name':this.state.name,
        'email':this.state.email,
        'city':this.state.city,
        'course':this.state.course,
        'phone':this.state.phone
    }

    //JSON.stringify()

//alert( JSON.stringify(formsdata) );

axios.post("http://localhost:3001/react173", formsdata).then();

}



    render() {
        return (<>
    <div className="container p-5">
                
            

            <form>
                    <div className="form-group">
                        <label>Name</label>
                        <input 
                        type="text" 
                        className="form-control" 
                        value={this.state.name}
                        onChange={this.HandleNameInput.bind(this) } />
                    </div>

                    <div className="form-group">
                        <label>Email</label>
                        <input 
                        type="email" 
                        className="form-control" 
                        value={this.state.email}
                        onChange={this.HandleEmailInput.bind(this) } />
                    </div>

                    <div className="form-group">
                        <label>City</label>
                        <input 
                        type="text" 
                        className="form-control" 
                        value={this.state.city}
                        onChange={this.HandleCityInput.bind(this) } />
                    </div>

                    <div className="form-group">
                        <label>Course</label>
                        <input 
                        type="text" 
                        className="form-control" 
                        value={this.state.course}
                        onChange={this.HandleCourseInput.bind(this) } />
                    </div>


                    <div className="form-group">
                        <label>Phone</label>
                        <input 
                        type="number" 
                        className="form-control" 
                        value={this.state.phone}
                        onChange={this.HandlePhoneInput.bind(this) } />
                    </div>
            

                    <button 
                    type="button" 
                    className="btn btn-primary"
                    onClick={this.HandleSubmitForm}>Submit</button>

            </form>

    </div>
        </>)
    }
}

export default Signup
