import React, { useState,useEffect } from 'react';
import axios from 'axios';
import Pager from './Pager'


//const url="https://jsonplaceholder.typicode.com/photos";

const url="https://jsonplaceholder.typicode.com/posts";


const PaginationTask=(props)=> {

    //State management
    const [Posts,setPosts]=useState([]);


    //Max_posts per Action Into View
    const [postsperAction,setpostsperAction]=useState(10);

    //By Default Button-1 Activated with 1-record to display First 1-10 posts
    const [defaultButtonpage,setdefaultButtonpage]=useState(1);



//RESTAPI_CALLS
useEffect(async ()=>{
    await axios.get(url).then( (apiresult)=> setPosts(apiresult.data) )
},[])

    return(<>
        <div className="container py-5">


            <Pager  page/>

            <div className="row">

                {
                     Posts.map( (res,index)=>{
                        return(
                        <div className="col-lg-4" key={index}>

                                <div className="card my-2">
                                   
                                    {/*<img src={res.thumbnailUrl} className="card-img-top" />*/}
                                   
                                    <div className="card-body">
                                        <h6 className="card-text">
                                                {res.id}.{res.title}
                                        </h6>

                                        <p classname="text-justify">
                                            {res.body}
                                        </p>
                                
                                    </div>
                                </div>
                        </div>
                        )
                    })
                }

            </div>


            
        </div>
    </>)
}

export default PaginationTask
