import React, { Fragment, useState,useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const HooksForms=()=>{
  
const [users,setUsers]=useState({
            name:'',
            email:'',
            location:'',
            course:'',
            phone:'',
        })
        
   
const { name, email,location,course,phone }=users;

let history=useHistory();


    //name_input
    const HandleNameInput=(e)=>
    {
        setUsers({ [e.target.name]: e.target.value })        
    }

    

    //email_input
    const HandleEmailInput=(e)=>
    {
        setUsers({ [e.target.name]: e.target.value })     
    }

    //city_input
    const HandleCityInput=(e)=>
    {
        setUsers({ [e.target.name]: e.target.value })     
    }

    //course_input
    const HandleCourseInput=(e)=>
    {
        setUsers({ [e.target.name]: e.target.value })     
    }


    //phone_input
    const HandlePhoneInput=(e)=>
    {
        setUsers({ [e.target.name]: e.target.value })     
    }



//submit_button
const HandleSubmitForm=async (e)=>
{

    e.preventDefault();

axios
.post("http://localhost:3003/react173", users)
.then( ()=>history.push('/hooksApi') )
}


return (<>
    <div className="container p-5">
                
    
            <form onSubmit={ this.HandleSubmitForm.bind(this) }>

                    <div className="form-group">
                        <label>Name</label>
                        <input 
                        type="text" 
                        className="form-control" 
                        name="name"
                        value={name}
                        onChange={HandleNameInput.bind(this) } />
                    </div>

                    <div className="form-group">
                        <label>Email</label>
                        <input 
                        type="email" 
                        className="form-control" 
                        name="email"
                        value={email}
                        onChange={HandleEmailInput.bind(this) } />
                    </div>

                    <div className="form-group">
                        <label>Location</label>
                        <input 
                        type="text" 
                        className="form-control" 
                        name="location"
                        value={location}
                        onChange={HandleCityInput.bind(this) } />
                    </div>

                    <div className="form-group">
                        <label>Course</label>
                        <input 
                        type="text" 
                        className="form-control" 
                        name="course"
                        value={course}
                        onChange={HandleCourseInput.bind(this) } />
                    </div>

                    <div className="form-group">
                        <label>Phone</label>
                        <input 
                        type="number" 
                        className="form-control" 
                        name="phone"
                        value={phone}
                        onChange={HandlePhoneInput.bind(this) } />
                    </div>
            
                    <button 
                    type="submit" 
                    className="btn btn-primary">Submit</button>

               
            </form>

    </div>
        </>)
 
}

export default HooksForms
