                  })
  }

  render() 
  {
      //Destructuring for State object properties
      const {id,name,email}=this.state;

      return(<>  

               <div class="container py-5">
                    <h3 className="text-center py-3">CLASS_COMPONENT_with_STATE_OBJECT</h3>

                    <p> U_ID is: { id } </p>

                    <p>U_NAME is: { name } </p>

                    <p>U_EMAIL is: { email } </p>

                    <br /> <br />


                    <form>
                    
                    <label>U_ID</label>
                    <input type="number" value={id} />  <br /><br />

                    <label>U_NAME</label>
                    <input type="text" value={name} />  <br /><br />


                    <label>U_EMAIL</label>
                    <input type="email" value={email} />  <br /><br />


                    <button type="button"
                          className="btn btn-info" 
                          onClick={  this.handleStateData }> 
                          Change Multiple_Properties 
                    </button>



                    </form>


                    


               </div>
            </>)
  }

}

