import Mernstack from './courses/'

const Conditionalrenderings=()=> {

    //JS_DATE_OBJECT
const today=new Date().getDay();

switch(today)
{
    case 0: return(<h1>Today is Sunday</h1>); break;
    case 1: return(<h1>Today is Monday</h1>); break;
    case 2: return(<h1>Today is Tuesday</h1>); break;
    case 3: return(<h1>Today is Wednesday</h1>); break;
    case 4: return(<h1>Today is Thursday</h1>); break;
    case 5: return(<h1>Today is Friday</h1>); break;
    case 6: return(<h1>Today is Saturday</h1>); break;
    default: return(<h1>Invalid Number </h1>)
}
   

