import React, { Component } from 'react'

//connection of react-redux
import { connect } from 'react-redux'

//Binding Actions & Reducers
import { bindActionCreators } from 'redux'

//actions
import { EmpActions, UsersAction }  from './../Actions/index.js'


//Share Redux data to React Component after Connection
import Display from './Display.js'

class ReactReduxConnection extends Component {
    
    constructor(props)
    {
        super(props);
    }


    componentDidMount()
    {
        this.props.EmpActions();
        this.props.UsersAction();
    }

    render() {


        return (<>

           
            <div>
                    <h2>USER_DATA</h2>
                    { 
                        
                            this.props.userdata.map( (res,index)=>{
                            
                            return(<ul key={index}>
                                    <li>{res.id}</li>
                                    <li>{res.name}</li>
                                    <li>{res.email}</li>
                                    <li>{res.phone}</li>
                            </ul>)
                            }) 
                }
                

                   {/*  
                    <Display emp={this.props.empdata} />
                      */}
   
            </div>
          
        </>)
    }
}

//Javascript_Function-> Collect State data from Store 
const mapStateToProps=(state)=>
{    
    console.log(state)
    
    //object-attribute
    return({ 
        empdata:state.EmpReducer,
        userdata:state.UsersAction
     })
   
}


const mapDispatchToProps=(dispatch)=>
{
    return bindActionCreators({ EmpActions, UsersAction }, dispatch);
}

export default connect(mapStateToProps,mapDispatchToProps)(ReactReduxConnection)
