import React, { Component } from 'react'

//connection of react-redux
import { connect } from 'react-redux'

//Binding Actions & Reducers
import { bindActionCreators } from 'redux'

//actions
import EmpActions from './../Actions/index.js'

//Share Redux data to React Component after Connection
import Display from './Display.js'

class ReactReduxConnection extends Component {
    
    constructor(props)
    {
        super(props);
    }


    componentDidMount()
    {
        this.props.EmpActions()//Redux_action_Function
    }


    render() {
        return (
            <div>
                         {/* <Display emp={ this.props.empdata } />    */} 

                         { 
                        
                this.props.empdata.map( (res,index)=>{
                    return(<ul key={index}>
                            <li>{res.id}</li>
                            <li>{res.name}</li>
                            <li>{res.email}</li>
                            <li>{res.phone}</li>
                    </ul>)
                    }) 
                }


            </div>
        )
    }
}

//Javascript_Function-> Collect State data from Store 
const mapStateToProps=(state)=>
{    
    console.log(state)
    return({  empdata:state.EmpReducer })
}


const mapDispatchToProps=(dispatch)=>
{
    return bindActionCreators({ EmpActions }, dispatch);
}

export default connect(mapStateToProps,mapDispatchToProps)(ReactReduxConnection)
