import React, { Component } from 'react'
//step-1
import axios from 'axios';

class AxoisHttpCalls extends Component {

    constructor()
    {
        super();
        this.state={ 
                    users:[] ////step-2
                    }

         console.log("***Constructor*****")
    }

    
    render() {
        console.log("***Render*****")
        return (<>

                <div className="container py-5">
                    <h3 className="py-3 text-center"> 
                    AXIOS RESTAPI CALLS.....  

                     </h3>

                   <table className="table table-dark mx-auto my-2">
                       <thead className="thead-light">
                           <tr>
                               <th>ID</th>
                               <th>NAME</th>
                               <th>USERNAME</th>
                               <th>EMAIL</th>
                               <th>PHONE</th>
                               <th colSpan="3">ACTION</th>
                           </tr>
                       </thead>

                      <tbody>   {/*  step-4 */}
                            {
                                this.state.users.map( (res,index)=>{
                                    return(<tr key={index}>
                                                    <td>{res.id}</td>
                                                    <td>{res.name}</td>
                                                    <td>{res.username}</td>
                                                    <td>{res.email}</td>
                                                    <td>{res.phone}</td>
                                                    <td> <button className="btn btn-primary text-white">View</button></td>
                                                    <td> <button className="btn btn-warning text-white">Edit</button></td>
                                                    <td> <button className="btn btn-danger text-white">Delete</button></td>
                                                
                                            </tr>)
                                })
                            }
                    </tbody>




                   </table>
                    
                        <br /><br /><br />
                       


                </div>

        </>)
    }


//step-3
    componentDidMount()
    {
        console.log("***componentDidMount*****");
        axios
        .get('https://jsonplaceholder.typicode.com/users')
        .then( (apiresult)=> this.setState({  users: apiresult.data }) )
       
    }



}

export default AxoisHttpCalls



